import {
    DATATYPES,
    PREFIXTYPES,
    PARAMETERNAMES,
    upperFirstLetter,
    sortCaseInsensitive,
    removePrefix,
    pluralize,
    ta2DataSource,
    ta3DataSource
} from './utils';
import $store from './store'
import axios from 'axios';

export const UPPERCASECATEGORY = {
    event: upperFirstLetter(DATATYPES.eventType.id),
    relation: upperFirstLetter(DATATYPES.relationType.id)
};

export const REQUEST = {
    get: 'GET',
    post: 'POST',
    delete: 'DELETE',
    put: 'PUT',
    patch: 'PATCH'
};

export const APIERRORMESSAGE = '. Please contact administrator.';
export const evalClient = axios.create({
    baseURL: location.protocol + '//' + location.hostname + ':5002/',
    timeout: 2000
});
export const s3Client = axios.create({
    baseURL: 'https://s3.amazonaws.com/aida-blackbox-media/html/',
    timeout: 2000
});
export const sinClient = axios.create({
    baseURL: process.env.VUE_APP_IS_DEV ? 'https://blackbox.verdi.nextcentury.com/solr/' : location.protocol + '//' + location.hostname + ':8983/solr/',
    timeout: 2000
});
export const EXCLUDEDDATASOURCES = [];

// invoke ui with 'npx vue-cli-service serve --mode dev'
export const BASE_URL = location.protocol + '//' + location.hostname + ':' + (process.env.VUE_APP_IS_DEV ? '' : '8008') + '/api/';

export default {
    getURLParams(params = {}) {
        let ret = new URLSearchParams();
        for ( const [key, value] of Object.entries(params)) {
            if(value) {
                ret.append(key, value);
            }
        }
        return ret;
    },
    fetchDataSourceList () {
        return this.apiRequest('graphs');
    },
    getCategoryArguments (namedGraph, category, text) {
        let params = {
            'graph': namedGraph
        };

        if(text) {
            params['argument'] = text;
        }
        return this.apiRequest(pluralize(category) + '/arguments', params);
    },
    getCategoryTypes (namedGraph, category) {
        let params = {
            'graph': namedGraph
        };

        return this.apiRequest(pluralize(category) + '/types', params);
    },
    getPrototypeArguments(prototypeId, graph) {
        return this.apiRequest('/roles', {
            id: prototypeId,
            graph: graph
        });
    },
    getClustersByCategory (namedGraph, category, limit) {
        return this.apiRequest(pluralize(category), {
            graph: namedGraph,
            dataLimit: limit
        });
    },
    getClustersByQueryParams(namedGraph, category, query, limit) {
        let params = {
            graph : namedGraph,
            dataLimit : limit
        };

        if(query[DATATYPES.argumentName.id]) {
            params['arguments'] = query[DATATYPES.argumentName.id];
        }

        if(query[category] && query[category].length > 0 && query[category][0] !== 'undefined') {
            params['types'] = query[category];
        }

        return this.apiRequest(pluralize(category), params);
    },
    getClusterDetails (id, category, namedGraph) {
        let params = {id: id};
        if(namedGraph) {
           params['graph'] = namedGraph;
        }
        return this.apiRequest(category, params);
    },
    getSinDocuments(id, namedGraph, hypothesis, roles) {
        let params = {id: id, hypothesis: hypothesis, roles: roles};
        if(namedGraph) {
            params['graph'] = namedGraph;
        }
        return this.apiRequest('docs', params);
    },
    getElementDetails (id, category) {
        return this.apiRequest(category + '/element', {id: id});
    },
    getEntityClusterDetails (id, limit) {
        return this.apiRequest('entity', {id: id, dataLimit: limit});
    },
    getEntityElementDetails (id) {
        return this.apiRequest('entity/element', {id: id});
    },
    getSinEventTypes (sinId, entityIds) {
        let params = {};
        if(Array.isArray(entityIds) && entityIds.length > 0) {
            params['entityIds'] = entityIds;
        }
        return this.apiRequest('sin/' + sinId + '/eventTypes', params);
    },
    getSinEntityNames (sinId, eventTypes, roleTypes) {

        let params = {};
        if(Array.isArray(eventTypes) && eventTypes.length > 0) {
            params['eventTypes'] = eventTypes;
        }

        if(Array.isArray(roleTypes) && roleTypes.length > 0) {
            params['roleTypes'] = roleTypes;
        }
        return this.apiRequest('sin/' + sinId + '/names', params);
    },
    getSinRoles(sinId, eventTypes, prototypeIds) {
        let params = {};
        if(Array.isArray(eventTypes) && eventTypes.length > 0) {
            params['eventTypes'] = eventTypes;
        }

        if(Array.isArray(prototypeIds) && prototypeIds.length > 0) {
            params['prototypeIds'] = prototypeIds;
        }

        return this.apiRequest('sin/' + sinId + '/roles', params);
    },
    getSinQueryResults(sinId, filters) {
        let params = '';
        if(Object.keys(filters).length > 0) {
            params = JSON.stringify(filters);
        }

        return this.apiRequest('sin/' + sinId  + '/search', params, REQUEST.post);
    },
    getAllSinEventTypes (roles, clusterIds) {
        let params = {};
        if(Array.isArray(roles) && roles.length > 0) {
            params['roleTypes'] = roles;
        }

        if(Array.isArray(clusterIds) && clusterIds.length > 0) {
            params['clusterIds'] = clusterIds;
        }
        return this.apiRequest('sin/eventTypes', null, REQUEST.post, JSON.stringify(params));
    },
    getAllSinRoles (eventTypes, clusterIds) {
        let params = {};
        if(Array.isArray(eventTypes) && eventTypes.length > 0) {
            params['eventTypes'] = eventTypes;
        }

        if(Array.isArray(clusterIds) && clusterIds.length > 0) {
            params['clusterIds'] = clusterIds;
        }

        return this.apiRequest('sin/roles', params);
    },
    getAllSinNames (eventTypes, roles) {
        let params = {};
        if(Array.isArray(eventTypes) && eventTypes.length > 0) {
            params['eventTypes'] = eventTypes;
        }

        if(Array.isArray(roles) && roles.length > 0) {
            params['roleTypes'] = roles;
        }

        return this.apiRequest('sin/names', params);
    },
    getAllSinQueryResults(filters) {
        let params = {};
        if(Object.keys(filters).length > 0) {
            params = filters;
        }

        return this.apiRequest('sin/search', null, REQUEST.post, JSON.stringify(params));
    },
    getClaimFrames(filters) {
        let params = {};
        if(Object.keys(filters).length > 0) {
            params = filters;
        }

        return this.apiRequest('claimframe/search', null, REQUEST.post, JSON.stringify(params));
    },

    getClaimFrameById(claimFrameId) {
        return this.apiRequest('claimframe/' + claimFrameId)
    },

    getClaimFrameFilterValues() {
        return this.apiRequest('claimframe/topics');
    },
    getGraphs() {
        let params = '';
        return this.apiRequest('graphs', params, REQUEST.get);
    },
    deleteGraph(graphURI) {
        let params = {};
        params['graphURI'] = graphURI;
        return this.apiRequest('graph', params, REQUEST.delete, null);
    },
    importGraph(graphURI, blob) {
        let params = {};
        params['graphURI'] = graphURI;

        let formData = new FormData();
        formData.append('fileName', blob);

        return this.apiRequest('graph', params, REQUEST.post, formData, true);
    },    
    renameGraph(graphURIOrig, graphURINew) {
        let params = {};
        params['graphURIOrig'] = graphURIOrig;
        params['graphURINew'] = graphURINew;
        return this.apiRequest('graph', params, REQUEST.put, null);
    },
    deleteCache(graphURI) {
        let params = {};
        if (graphURI) {
            params['graphURI'] = graphURI;
        } else {
            params = null;
        }
        return this.apiRequest('cache', params, REQUEST.delete, null);
    },
    cacheGraph(payload) {
        return this.apiRequest('cache', null, REQUEST.post, payload);
    },
    apiRequest(api, parameters, requestType=REQUEST.get, payload=null, noContentTypeHeader=false) {
        let url = BASE_URL + api;
        let request = { method: requestType };
        let dataObject = {data:{}, statusMessage:''};

        url += (parameters ? '?' + this.getURLParams(parameters) : '');
        request['headers'] = {'Accept': 'application/json'};

        if (payload) {
            request['body'] = payload;
        }

        //TODO: FIX THIS, header must be null for formData
        if (!noContentTypeHeader) {
            request['headers'] = {'Content-Type': 'application/json; charset=utf-8'};
        }

        let promise = fetch(url, request);

        return promise.then((response) => {
            if(response.ok) {
                return response.json();
            } else {
                throw new Error(response.statusText);
            }
        }).then((response) => {
            dataObject.data = response;
            return dataObject;
        }).catch((error) => {
            dataObject.statusMessage = error.toString() + APIERRORMESSAGE;
            return dataObject;
        });
    },
    transformParamFormat(parameters) {
        let formattedParams = {};
        let categoryTypes = [DATATYPES.eventType.id, DATATYPES.relationType.id];
        let conditionalTypes = [DATATYPES.entityType.id, DATATYPES.argumentName.id, DATATYPES.kbId.id];

        for (const parameter in parameters) {
            if(parameters.hasOwnProperty(parameter)) {
                const isObject = typeof parameters[parameter] === 'object';

                //Checks for relation or event filters that include nested objects and returns its values as an array.
                //Checks for relation, event, or conditional filters that contain string values and returns an array of the string values.
                //Checks for conditional filters that contain a "values" object and returns its values as an array.
                // All other filter/query values are returned as is.
                formattedParams[parameter] = categoryTypes.includes(parameter) && isObject ? [...Object.values(parameters[parameter])] :
                    (categoryTypes.includes(parameter) || conditionalTypes.includes(parameter)) && !isObject ?  [parameters[parameter]] :
                    conditionalTypes.includes(parameter) && parameters[parameter].values ? { condition : parameters[parameter].condition, values : [...Object.values(parameters[parameter].values)]} :
                    parameters[parameter];
            }
        }

        return formattedParams;
    },
    compileApiParams() {
        let apiParams = {};
        let queryState = $store.getters.getQueryParams;

        if(queryState.url.params && queryState.url.params.term) {
            apiParams[PARAMETERNAMES.term] = queryState.url.params.term;
        }

        //Set the datasource for this object
        if(queryState.currentDataSource) {
            apiParams[PARAMETERNAMES.datasoure] = queryState.currentDataSource;
        }

        //Verify that filters have been submitted and add all filters to this object.
        if(Object.keys(queryState.filter).length > 0) {
            apiParams = {...apiParams, ...this.transformParamFormat(queryState.filter)};

            //Set the filter id for the entire query in order to save the query to a database
            if(queryState.url.params && queryState.url.params.filter) {
                apiParams['filter_id'] = queryState.url.params.filter;
            }
        }

        //Verify that a search bar term has been submitted and add the query values to this object
        if(Object.keys(queryState.term).length > 0) {

            const searchKey = queryState.term.type;
            if(searchKey) {
                //transforms term keys into a key-value pair where type becomes the key and input becomes an array
                let formattedParams = {[searchKey]: [queryState.term.input]};

                //If the searchKey exists in apiParams, concatenate the term input array with the existing apiParams arrays. Otherwise define the searchKey object
                apiParams = {
                    ...apiParams, [searchKey]:
                        apiParams[searchKey] ? [...formattedParams[searchKey], ...apiParams[searchKey]] : formattedParams[searchKey]
                };
            }
        }
        return apiParams;
    },
    setInitialData() {
        this.fetchDataSourceList().then((response) => {
            if(response.data && response.data.length > 0) {
                const sourceList = sortCaseInsensitive([...new Set(response.data.reduce((sources, s) => {
                    const charCount = s.split('/').length - 1;
                    const source = charCount === 5 ? s : s.substring(0, s.lastIndexOf('/'));
                    if(!EXCLUDEDDATASOURCES.includes(source)) {
                        sources.push(source);
                    }
                    return sources;
                }, []))]);


                $store.commit('setDefaultKbDataSource', sourceList.find(d => ta2DataSource(d)));
                $store.commit('setDefaultHypothesisDataSource', sourceList.find(d => ta3DataSource(d)));
                $store.dispatch('updateDataSourceProperties', sourceList);
                this.setCategoryData();
            }
        });
    },
    setCategoryData(source) {
        for (let dataType of [DATATYPES.eventType.id, DATATYPES.relationType.id]) {
            this.setCategoryArguments(dataType, source);
            this.setCategoryTypes(dataType,source);
        }
    },
    formatArgumentStrings(data) {
        //remove double quotes in the argument strings
        let nameList = data.map(value => {
            return value.replace(/"/g, '');
        });

        //remove duplicates and case insensitive sort of values
        return sortCaseInsensitive([...new Set(nameList)]);
    },
    formatCategoryTypes(data) {
        return sortCaseInsensitive(data.map(value => {
            return removePrefix(value, PREFIXTYPES.hash);
        }));
    },
    setCategoryArguments(category, source = $store.state.defaultKbDataSource) {
        this.getCategoryArguments(
            source,
            category
        ).then((response) => {
            if (response.data && response.data.length > 0) {
                const items = this.formatArgumentStrings(response.data);
                $store.dispatch('updateCategoryArgumentsProperties', {category:category, arguments: items})
            }
        });
    },
    setCategoryTypes(category, source = $store.state.defaultKbDataSource) {
        this.getCategoryTypes(
            source,
            category
        ).then((response) => {
            if (response.data && response.data.length > 0) {
                const items = this.formatCategoryTypes(response.data);
                $store.commit('updateCategoryTypes', {category:category, types: items})
            }
        });
    }
}
